import unittest

class TestSwapVariables(unittest.TestCase):
    def test_integers(self):
        self.assertEqual(swap_variables({'a': 1, 'b': 2}), {'a': 2, 'b': 1})

    def test_strings(self):
        self.assertEqual(swap_variables({'a': 'hello', 'b': 'world'}), {'a': 'world', 'b': 'hello'})

    def test_mixed_types(self):
        self.assertEqual(swap_variables({'a': 1, 'b': 'two'}), {'a': 'two', 'b': 1})

    def test_same_values(self):
        self.assertEqual(swap_variables({'a': 5, 'b': 5}), {'a': 5, 'b': 5})

    def test_none_values(self):
        self.assertEqual(swap_variables({'a': None, 'b': 42}), {'a': 42, 'b': None})

    def test_original_unmodified(self):
        original = {'a': 10, 'b': 20}
        result = swap_variables(original)
        self.assertEqual(original, {'a': 10, 'b': 20})
        self.assertEqual(result, {'a': 20, 'b': 10})

    def test_returns_new_dict(self):
        original = {'a': 1, 'b': 2}
        result = swap_variables(original)
        self.assertIsNot(result, original)

